INSERT INTO `prod_cat` VALUES ('4', 'Смесители роторные', '/products/categories/', 'Смесители роторные, смешивание сыпучих материалов, смесители промышленные, шихтоподготовка, шихтоподготовка агломерационных, шихтоподготовка окомковательных, смешивание концентратов, смешивание углей, смешивание сырья, изготовление смесителей роторных, роторные смесители, производство роторных смесителей, смешивание материалов на конвеере', 'Производство смесителей роторных для смешивания сыпучих материалов с насыпной плотностью до 2,5 т/м3 на движущейся ленте ленточного конвейера.');
